["5024_4"] = {id="5024_4", desc={}, stepType=4, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5024_5"] = {id="5024_5", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="太好了，有了天赋，不同的小伙伴互相搭配会有不同的效果，加油哦。"}}, stepType=5, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5005_1"] = {id="5005_1", desc={}, stepType=1, param={}, active=0, type=1, finishStep=1, jump=0, mustExe=0, },
["5005_2"] = {id="5005_2", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="神兵开启了，队长大人。"}}, stepType=2, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5006_1"] = {id="5006_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长，您的小伙伴们已经很厉害啦，现在可以去挑战<font color='255,0,0'>世界BOSS</font>大蛇复制体了！记住哦，每天晚上21.30开启。战胜大蛇复制体会有好多好多的银币呢！"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5007_1"] = {id="5007_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="技能开启<font color='255,0,0'>克制奥义</font>，所有技能赋予力量奥义，可对敌方英雄产生克制伤害。队长要不咱们瞅瞅？"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5008_1"] = {id="5008_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="英雄属性分为：风、地、雷、炎、暗。每个英雄的技能会对不同系的英雄产生克制，下面就由伦家给你详细讲一下吧！"}}, stepType=1, param={}, active=1, type=16, finishStep=1, jump=0, mustExe=0, },
["5008_2"] = {id="5008_2", desc={"每个英雄都有一个属性。"}, stepType=2, param={}, active=0, type=17, finishStep=0, jump=0, mustExe=0, },
["5008_3"] = {id="5008_3", desc={"技能会对英雄的属性进行克制。"}, stepType=3, param={}, active=0, type=18, finishStep=0, jump=0, mustExe=0, },
["5008_4"] = {id="5008_4", desc={"技能上有显示克制风，就是对所有风系英雄时有额外伤害加成。"}, stepType=4, param={}, active=0, type=18, finishStep=0, jump=0, mustExe=0, },
["5008_5"] = {id="5008_5", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="是不是很难记住！本萌妹也是这样觉得！交队长个简单的方法，战斗之前看对方的属性图标是什么，就上对应克制的技能就好啦！"}}, stepType=5, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5009_1"] = {id="5009_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="大蛇的死忠们最近蠢蠢欲动，似乎有大动作。队长，赶紧去<font color='255,0,0'>大蛇八杰</font>敲打敲打他们。挑战成功后，会获得大量的奖励哦。"}}, stepType=1, param={}, active=0, type=16, finishStep=1, jump=0, mustExe=0, },
["5009_2"] = {id="5009_2", desc={}, stepType=2, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5009_3"] = {id="5009_3", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="大蛇八杰奖励“技能碎片”有了技能碎片我们就可以激活新技能啦，让我们先挑战下试试。"}}, stepType=3, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5009_4"] = {id="5009_4", desc={}, stepType=4, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5009_5"] = {id="5009_5", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="天国神族受到了诅咒，本方上阵的英雄（不包括援助英雄）挑战胜利后，是无法在该层挑战其他BOSS的，队长大人要特别注意哦！"}}, stepType=5, param={}, active=1, type=16, finishStep=0, jump=0, mustExe=0, },
["5011_1"] = {id="5011_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长！是时候参加一波真枪实弹的<font color='255,0,0'>世界巡回赛</font>了。每挑战成功一位玩家，就可以领悟一份大宝藏！越到后面奖励越豪华哦！"}}, stepType=1, param={}, active=0, type=16, finishStep=1, jump=0, mustExe=0, },
["5011_2"] = {id="5011_2", desc={}, stepType=2, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5011_3"] = {id="5011_3", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="在世界巡回赛里，是无法回血和能量的哦，请谨慎出战，加油哦。"}}, stepType=3, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5012_1"] = {id="5012_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长！<font color='255,0,0'>噩梦模式</font>开启啦。听说里面有好东东哦。有空就去看看吧。"}}, stepType=1, param={}, active=1, type=16, finishStep=1, jump=0, mustExe=0, },
["5012_2"] = {id="5012_2", desc={}, stepType=2, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5012_3"] = {id="5012_3", desc={}, stepType=3, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5012_4"] = {id="5012_4", desc={"<font color='72,28,16'>噩梦难度会产出指定的英雄碎片哦，加油哦。</font>"}, stepType=4, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5013_1"] = {id="5013_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="<font color='255,0,0'>鲜花系统</font>开启啦。队长可以购买鲜花赠送给其他小伙伴。赠送别人鲜花会获得<font color='255,0,0'>体力</font>哦。如果队长能收到别人送的鲜花，会有礼物哦！看看队长魅力有多大吧！"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5014_1"] = {id="5014_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长可花费少量的金币购买鲜花赠送给予其他玩家。您会获得一定的体力，对方也会获得一定的奖励哦。赠人玫瑰，手有余香，快去吧！"}}, stepType=1, param={}, active=1, type=16, finishStep=0, jump=0, mustExe=0, },
["5015_1"] = {id="5015_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="当当当当！<font color='255,0,0'>战役</font>里第二个道场开启了。道场里有大量的英雄经验哦！想要提升英雄的等级。就跟第二个道场里的英雄过过招吧。"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5016_1"] = {id="5016_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="偷偷的告诉队长，使用破招型英雄会额外对他们造成的伤害。只能告诉你这么多啦，快点谢谢伦家！"}}, stepType=1, param={}, active=1, type=16, finishStep=0, jump=0, mustExe=0, },
["5017_1"] = {id="5017_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="听说挑战成功<font color='255,0,0'>战役</font>里第三个道场的英雄后，会获得一些珍贵的<font color='255,0,0'>怒气点</font>以及<font color='255,0,0'>援助点</font>哦。以队长的实力自然不在话下啦。现在第三个道场已经开启了，还不赶紧来一发？"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5018_1"] = {id="5018_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长大人，在本场景中，是无法使用怒气技能的，但如果您上阵<font color='255,0,0'>【普攻型】</font>的英雄会有额外的伤害哦。"}}, stepType=1, param={}, active=1, type=16, finishStep=0, jump=0, mustExe=0, },
["5019_1"] = {id="5019_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="队长一个人玩是不是有点空虚寂寞冷啊。木有关系，<font color='255,0,0'>公会系统</font>开放了。从此你就不素一个人！公会内<font color='255,0,0'>可调酒、可德州、可踢馆</font>。趣味多多、惊喜满满哦！"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5020_1"] = {id="5020_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="<font color='255,0,0'>夺宝</font>已经开启啦，挖矿！打人！抢宝藏！真是一个非常NICE的玩法呀。"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5021_1"] = {id="5021_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="宝藏每一段时间就会产出一些宝物，宝藏等级越高，获得的宝物就越丰富。队长您可以占领两个宝藏。不过占领成功后要派出英雄进行驻守哦，不然会被别人偷袭的。"}}, stepType=1, param={}, active=1, type=16, finishStep=0, jump=0, mustExe=0, },
["5038_1"] = {id="5038_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="让我们看看VIP专属副本吧！"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["5038_2"] = {id="5038_2", desc={}, stepType=2, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5038_3"] = {id="5038_3", desc={}, stepType=3, param={}, active=0, type=1, finishStep=0, jump=0, mustExe=0, },
["5038_4"] = {id="5038_4", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="不同的VIP等级会有不同的副本哦，级别越高通关奖励越高！"}}, stepType=4, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
["8000_1"] = {id="8000_1", desc={{iconName="yindaoyun1",scale=1,dir=1,desc="亲爱的队友，每个英雄您只能拥有一个。碎片达到一定的数量可以<font color='255,0,0'>升星</font>，英雄形象边框颜色将随之改变。"}}, stepType=1, param={}, active=0, type=16, finishStep=0, jump=0, mustExe=0, },
